import React, { useState } from 'react'
import SignUpinfo from './SignUpinfo'
import PersonalInfo from './PersonalInfo'
import OtherInfo from './OtherInfo'

function Form() {
  const [page, setPage] = useState(0) //first form = 0

  const FormTitles = ['Sign Up', 'Personal Info', 'Other']

  const PageDisplay = () => {
    if (page === 0) {
      return <SignUpinfo />
    } else if (page === 1) {
      return <PersonalInfo />
    } else {
      return <OtherInfo />
    }
  }

  return (
    <div className="form">
      <div className="progressbar"></div>
      <div className="form-container">
        <div className="header">
          <h1>{FormTitles[page]}</h1>
        </div>
        {/* calling the function inside of here below, which return PageDisplay  */}
        <div className="body">{PageDisplay()}</div>

        <div className="footer">
          <button
            disabled={page === 0}
            onClick={() => {
              setPage(setPage((currPage) => currPage - 1))
            }}
          >
            Prev
          </button>
          <button
            disabled={page === FormTitles.length - 1}
            onClick={() => {
              setPage(setPage((currPage) => currPage + 1))
            }}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  )
}

export default Form
